import "./progressBar.css";
import React, { useState } from "react";

function ProgressBar({ num }) {
  // const [number, setNumber] = useState(0);
  const [range, setRange] = useState(percentRange(num));

  const percentRange = (value) => {
    if (value < 25) return { range: "0% - 25%", color: "#EE887E" };
    else if (value >= 25 && value < 50)
      return { range: "25% - 50%", color: "#EE887E" };
    else if (value >= 50 && value < 75) return "50% - 75%";
    else if (value >= 75 && value <= 100) return "75% - 100%";
  };

  return (
    <div className="progressBar_container">
      <div className="progressBar_header">
        <div>{percentRange(num)}</div>
        {/* <input
          type="number"
          value={num}
          min="0"
          max="100"
          className="input_form"
          onChange={(e) => setNumber(e.target.value)}
        /> */}
        <div>{num}</div>
      </div>
      <progress value={num} max="100">
        `${num}%`
      </progress>
    </div>
  );
}

export default ProgressBar;
