import "./donutChart.css";
import React from "react";
import { PieChart, Pie, Sector, Cell, ResponsiveContainer } from "recharts";

// const data = [
//   { name: "0% - 25%", value: 10 },
//   { name: "25% - 50%", value: 20 },
//   { name: "50% - 75%", value: 10 },
//   { name: "75 - 100%", value: 10 },
// ];

const COLORS = ["#F2A71E", "#5AC380", "#4DA1FF", "#BFBFD0"];

const setColors = () => {
  if (value < 25) return { span: "0% - 25%", color: "#EE887E" };
  else if (value >= 25 && value < 50)
    return { span: "25% - 50%", color: "#EE887E" };
  else if (value >= 50 && value < 75)
    return { span: "50% - 75%", color: "#7BBFE8" };
  else if (value >= 75 && value <= 100)
    return { span: "75% - 100%", color: "#5AC380" };
};

export default function DonutChart({ data }) {
  return (
    <div className="donutChart_container">
      <PieChart width={182} height={182}>
        <Pie
          data={data}
          innerRadius={"90%"}
          outerRadius={"100%"}
          fill="#8884d8"
          paddingAngle={1}
          dataKey="value"
          isAnimationActive={false}
        >
          {data.map((item, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
      </PieChart>
      <div className="donutChart_label">
        {data.map((item, index) => {
          return (
            <div className="label_item">
              <div></div>
              <div>
                <strong>{item.value} |</strong> {item.name}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
