import "./progressBar.css";
import React, { useState } from "react";

function ProgressBar({ num }) {
  // const [number, setNumber] = useState(0);
  const [barColor, setBarColor] = useState("#EE887E");

  const percentRange = (value) => {
    if (value < 25) return "0% - 25%";
    else if (value >= 25 && value < 50) return "25% - 50%";
    else if (value >= 50 && value < 75) return "50% - 75%";
    else if (value >= 75 && value <= 100) return "75% - 100%";
  };

  return (
    <div className="progressBar_container">
      <div className="progressBar_header">
        <div>{percentRange(number)}</div>
        <input
          type="number"
          value={num}
          min="0"
          max="100"
          className="input_form"
          onChange={(e) => setNumber(e.target.value)}
        />
      </div>
      <progress value={number} max="100">
        `${number}%`
      </progress>
    </div>
  );
}

export default ProgressBar;
