import "./App.css";
import React, { useState } from "react";

function App() {
  const [number, setNumber] = useState(0);
  const [barColor, setBarColor] = useState("#EE887E");

  const percentRange = (num) => {
    if (num < 25) return "0% - 25%";
    else if (num >= 25 && num < 50) return "25% - 50%";
    else if (num >= 50 && num < 75) return "50% - 75%";
    else if (num >= 75 && num <= 100) return "75% - 100%";
  };

  return (
    <div className="App">
      {/* Progress Bar */}
      <div>
        <div>
          <div>{percentRange(number)}</div>
          <input value={number} onChange={(e) => setNumber(e.target.value)} />
        </div>
        <progress id="file" value="number" max="100" />
      </div>
    </div>
  );
}

export default App;
